sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) { // 2020.12.01. 15:35
var body = {};
if (request.body.data.AssetSynchronizeRequest == null) {
	body.TransactionID = body.AssetNumber = body.SerialNumber = '';
	body.Message = 'Invalid request.';
	body.Succcess = body.Success = false;
} // if
else {
	var reqData = request.body.data.AssetSynchronizeRequest;
	if (reqData.TransactionID == null) {
		body.TransactionID = '';
	} // if
	else {
		body.TransactionID = reqData.TransactionID;
	} // else
	body.AssetNumber = reqData.Asset.AssetNumber;
	body.SerialNumber = reqData.Asset.SerialNumber;
	body.Message = '';
	if ((reqData.Asset.AssetNumber == null) || (reqData.Asset.SerialNumber == null)) {
		if (reqData.Asset.AssetNumber == null) {
			body.Message = 'AssetNumber must not be blank.';
			body.AssetNumber = '';
		} // if
		if (reqData.Asset.SerialNumber == null) {
			if (body.Message != '') {
				body.Message += '  ';
			} // if
			body.Message += 'SerialNumber must not be blank.';
			body.SerialNumber = '';
		} // if
		body.Succcess = body.Success = false;
	} // if
	else {
		body.Message = 'The asset has been updated.';
		body.Succcess = body.Success = true;
	} // else
} // else
response.setContentType('application/json');
response.setStatus(200);
response.setBody(body);
})(request, response);
