sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
// implement resource here
var reqData = request.body.data;
var transactionID;
var action;
var incidentNumber;
var accountName;
var summary;
var incidentDescription;
var something;
if (reqData instanceof Array) {
 transactionID = reqData[0].TransactionID;
 action = reqData[0].Action;
 incidentNumber = reqData[0].IncidentNumber;
 accountName = reqData[0].AccountName;
 summary = reqData[0].Summary;
 incidentDescription = reqData[0].Description;
} // if
else {
 transactionID = reqData.TransactionID;
 action = reqData.Action;
 incidentNumber = reqData.IncidentNumber;
 accountName = reqData.AccountName;
 summary = reqData.Summary;
 incidentDescription = reqData.Description;	
} // else

var act = action.toUpperCase();
switch (action.toUpperCase()) {
  case 'CREATE':
	something = 'It is a Create request.';
	break;

  case 'UPDATE':
	something = 'It is a Update request.';
	break;

  case 'REASSIGN':
	something = 'It is a Reassign request.';
	break;

  case 'RESOLVE':
	something = 'It is a Resolve request.';
	break;

  case 'CLOSE':
	something = 'It is a Close request.';
	break;

  case 'CANCEL':
	something = 'It is a Cancel request.';
	break;

	default:
	something = 'Invalid Action.';
} // switch

response.setContentType('application/json');
response.setStatus(200);
var body = {};
body.TransactionID = transactionID;
body.Action = action;
body.ActionValidity = something;
body.IncidentNumber = incidentNumber;
body.AccountName = accountName;
body.Summary = summary;
body.Description = incidentDescription;	
response.setBody(body);	
})(request, response);
