    gr.close_notes = clsnote;
    gr.u_vendor_ticket = ventic;
    gr.u_xerox_action = xaction;
    gr.u_xerox_message = xmess;
    gr.u_xerox_ticketnumber = xticnum;
    gr.u_xerox_transaction_id = xtranid;
*/	
    gr.update();

    var body = {};
/*AA 
    body.TransactionID = gr.u_xerox_transaction_id;
    body.TicketNumber = gr.sys_id;
    body.IncidentNumber = gr.u_vendor_ticket;
    body.ServiceStatus = gr.u_xerox_servicestatus;
    body.Succcess = "true";
